INSERT INTO `emails` VALUES (1,'resetPassword','Configure e-mail message that is sent to recover the forgotten password','Reset your password','<p>Dear {{name}},</p>\n<p>Please <a href=\"{{resetLink}}\">click here</a> to reset your password.</p>\n<p>If you are not able to click on link please copy and paste: {{resetLink}} in your browser.</p>\n<p>Regards,</p>\n<p>Team NiNaCoder.</p>',NULL,'2020-07-02 22:35:53'),(2,'verifyAccount','Configure e-mail message that is sent to activate your account','{{name}}! Verify your account','<div>Dear {{name}},</div>\n<div>&nbsp;</div>\n<div>You&rsquo;re almost there. Confirm your account below to finish creating your account.</div>\n<div>Please&nbsp;<a href=\"{{validationLink}}\" data-name=\"Apple Music Toolbox\" data-type=\"url\">click here</a>&nbsp;to confirm your account.</div>\n<div>&nbsp;</div>\n<div>If you are not able to click on link please copy and paste: {{validationLink}} in your browser.</div>\n<div>&nbsp;</div>\n<div>Regards,</div>\n<div>Team Music Engine.</div>',NULL,'2020-09-07 07:27:20'),(3,'newUser','Configure e-mail message that is sent to welcome new user','Welcome, We\'re so Glad You\'re Here','<p>{{name}}, Welcome to NiNa Sound</p>\n<p>Play and discover music you love on your phone for FREE.</p>\n<p>Regards,</p>\n<p>Team NiNaCoder.</p>',NULL,'2019-08-24 03:03:33'),(4,'approvedArtist','Configure e-mail message that is sent to welcome new artist','Your claiming to access artist has been approved!','<p>Your claiming to access artist has been approved!</p>\n<p>Hi {{name}},</p>\n<p>Congratulations! Your claming to access artist \"{{artist_name}}\" has been approved.</p>\n<p>To start uploading songs/albums, get logging in to your account you will see a new menu Aritst/Band Management right on your account profile.</p>\n<p>Regards,</p>\n<p>Music Engine Team</p>',NULL,'2020-09-07 07:23:07'),(5,'rejectedArtist','Configure e-mail message that is sent when an artist claim request has been rejected','You claiming request has been rejected','<p>Hi, {{name}}</p>\n<p>&nbsp;</p>\n<p>Thank you for your submission. We have completed our review of your claming for accessing the artist \"{{artist_name}}\".</p>\n<p>After carefully review we are deceived to not accept your request&nbsp; and you won\'t be able to re-claim this artist again.</p>\n<p>{{comment}}</p>\n<p>Thank for every!</p>\n<p>All the best!</p>\n<p>Music Engine team</p>',NULL,'2020-09-07 07:20:24'),(6,'subscribePlaylist','Configure e-mail message that when some one subscribe a playlist','{{friendName}} just subscribed your playlist','<p>Hi {{name}}</p>\n<p>{{friendName}} just subscribed your playlist.</p>',NULL,'2020-09-07 07:24:23'),(7,'shareMedia','Configure e-mail message that when some one share media','{{friendName}} just shared something with you.','<p>H, {{name}}</p>\n<p>{{friendName}} just shared something with you.</p>',NULL,'2020-09-07 07:25:06'),(8,'followUser','Configure e-mail message that when people following each other','{{friendName}} is now following you','<p>Hi {{name}}</p>\n<p>{{friendName}} is now following you</p>',NULL,'2020-09-07 07:25:34'),(9,'newComment','Configure e-mail message that is sent when a new comment is posted on the site','New comment has been posted','<p>Dear Admin,</p>\n<p>The comment for the article that you have subscribed to was added on your site</p>\n<p>------------------------------------------------</p>\n<p><strong>Summary of the comment</strong></p>\n<p>------------------------------------------------</p>\n<p>Author: {{name}}</p>\n<p>Author username: {{username}}</p>\n<p>Date: {{created_at}}</p>\n<p>Link to the object: <a href=\"{{url}}\" target=\"_blank\" rel=\"noopener\">{{url<span style=\"font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Oxygen, Ubuntu, Cantarell, \'Open Sans\', \'Helvetica Neue\', sans-serif;\">}}</span></a></p>\n<p>------------------------------------------------</p>\n<p><strong>Comment text</strong></p>\n<p>------------------------------------------------</p>\n<p>{{text}}</p>\n<p>------------------------------------------------</p>\n<p>&nbsp;</p>\n<p>NiNCoder Team</p>','2019-08-20 17:00:00','2020-07-15 23:28:31'),(10,'approvedSong','Configure e-mail message that is sent when a song has been approved','Your song, {{title}}, has been approved','<p>Your song has been approved!</p>\n<p>Hi {{name}},</p>\n<p>Congratulations! Your song \"{{title}}\" has been approved. You can view it online here:</p>\n<p><a href=\"{{url}}\" target=\"_blank\" rel=\"noopener\">{{url}}</a></p>\n<p>Thanks for your high quality submission. Keep up the awesome work!</p>\n<p>Regards,</p>\n<p>NiNaCoder Team</p>',NULL,'2020-07-15 03:01:27'),(11,'approvedAlbum','Configure e-mail message that is sent when an album has been approved','Your album, {{title}}, has been approved!','<p>Your album has been approved!</p>\n<p>Hi {{name}},</p>\n<p>Congratulations! Your album&nbsp;\"{{title}}\" has been approved. You can view it online here:</p>\n<p><a href=\"{{url}}\" target=\"_blank\" rel=\"noopener\">{{url}}</a></p>\n<p>Thanks for your high quality submission. Keep up the awesome work!</p>\n<p>Regards,</p>\n<p>NiNaCoder Team</p>',NULL,'2020-07-15 03:01:13'),(12,'rejectedSong','Configure e-mail message that is sent when a song has been rejected','Your song, {{title}}, has been rejected','<p>Hi, {{name}}</p>\n<p>&nbsp;</p>\n<p>Thank you for your submission. We have completed our review of \"{{title}},\" and unfortunately we found it isn\'t at the quality standard required to move forward, and you won\'t be able to re-submit this song again.</p>\n<p>{{comment}}</p>\n<p>We appreciate the effort and time you\'ve put into creating your song. And we\'d be happy to help make sure your next entry will meet our submission requirements. Here\'s our advice:</p>\n<p>Visit our forums and ask fellow authors for feedback. Our helpful community will be glad to lend a hand.</p>\n<p>Check out this Help Centre article to understand why and how items get rejected.</p>\n<p>We hope to see a new submission from you soon!</p>\n<p>All the best!</p>\n<p>NiNaCoder team</p>',NULL,'2020-07-15 02:58:15'),(13,'rejectedAlbum','Configure e-mail message that is sent when an album has been rejected','Your album, {{title}}, has been rejected','<p>Hi, {{name}}</p>\n<p>&nbsp;</p>\n<p>Thank you for your submission. We have completed our review of \"{{title}},\" and unfortunately we found it isn\'t at the quality standard required to move forward, and you won\'t be able to re-submit this album again.</p>\n<p>{{comment}}</p>\n<p>We appreciate the effort and time you\'ve put into creating your song. And we\'d be happy to help make sure your next entry will meet our submission requirements. Here\'s our advice:</p>\n<p>Visit our forums and ask fellow authors for feedback. Our helpful community will be glad to lend a hand.</p>\n<p>Check out this Help Centre article to understand why and how items get rejected.</p>\n<p>We hope to see a new submission from you soon!</p>\n<p>All the best!</p>\n<p>NiNaCoder team</p>',NULL,'2020-07-15 02:58:00'),(14,'subscriptionReceipt','Configure e-mail message that is sent when a subscription has been placed.','Your receipt from ‪‬ #{{receipt_id}}','<div style=\"margin: 0; padding: 0; border: 0; background-color: #f1f5f9; font-family: -apple-system,BlinkMacSystemFont,\'Segoe UI\',Roboto,\'Helvetica Neue\',Ubuntu,sans-serif; min-width: 100%!important; width: 100%!important;\">\n<div style=\"display: none!important; max-height: 0; max-width: 0; overflow: hidden; color: #ffffff; font-size: 1px; line-height: 1px; opacity: 0;\">&nbsp;</div>\n<div style=\"min-width: 100%; width: 100%; background-color: #f1f5f9;\">\n<table class=\"m_-8504956366380948631Wrapper\" style=\"border: 0; border-collapse: collapse; margin: 0 auto!important; padding: 0; max-width: 600px; min-width: 600px; width: 600px;\" align=\"center\">\n<tbody>\n<tr>\n<td style=\"border: 0; border-collapse: collapse; margin: 0; padding: 0;\">\n<table class=\"m_-